import org.apache.commons.dbutils.DbUtils;

import javax.naming.InitialContext;
import javax.naming.NamingException;
import javax.servlet.ServletException;
import javax.servlet.annotation.WebServlet;
import javax.servlet.http.HttpServlet;
import javax.servlet.http.HttpServletRequest;
import javax.servlet.http.HttpServletResponse;
import javax.servlet.http.HttpSession;
import javax.sql.DataSource;
import java.io.IOException;
import java.sql.Connection;
import java.sql.PreparedStatement;
import java.sql.ResultSet;
import java.sql.SQLException;
import java.util.UUID;
import java.util.regex.Pattern;

@WebServlet(description = "User Sign In", urlPatterns = {"/sign", "/signin", "/signup", "/signout"}, loadOnStartup = 1)
public class Sign extends HttpServlet {
    @Override
    protected void doGet(HttpServletRequest req, HttpServletResponse resp) throws ServletException, IOException {
        req.getRequestDispatcher("sign.jsp").forward(req, resp);
    }

    //注册
    @Override
    protected void doPost(HttpServletRequest req, HttpServletResponse resp) throws IOException {
        SignUpField jsonReq = JsonTool.fetch(req, SignUpField.class);
        ResponseField jsonRes;

        if (!jsonReq.Vaild()) {
            jsonRes = new ResponseField("Failed", "Invalid Parameter");
            JsonTool.response(resp, jsonRes);
            return;
        }

        UUID uuid = UUID.randomUUID();
        String salt = SHA256.hash256(uuid.toString());
        String saltPass = SHA256.hash256(salt + jsonReq.Password);
        Connection conn = null;
        PreparedStatement stmt = null;
        try {
            DataSource ds = (DataSource) new InitialContext().lookup("java:comp/env/jdbc/postgres");
            assert ds != null;
            conn = ds.getConnection();
            stmt = conn.prepareStatement("INSERT INTO public.user (mail,nickname,salt,salt_pass) VALUES (?,?,?,?);");
            stmt.setString(1, jsonReq.Mail);
            stmt.setString(2, jsonReq.Nickname);
            stmt.setString(3, salt);
            stmt.setString(4, saltPass);
            stmt.executeUpdate();
            jsonRes = new ResponseField("Success", "注册成功");
        } catch (SQLException e) {
            switch (e.getSQLState()) {
                case "23505":
                    jsonRes = new ResponseField("Failed", "用户名重复");
                    break;
                default:
                    jsonRes = new ResponseField("Failed", e.getMessage(), String.format("%s", e.getSQLState()));
            }
        } catch (NamingException e) {
            e.printStackTrace();
            jsonRes = new ResponseField("Failed", "Context NamingException", e.getExplanation());
        } finally {
            DbUtils.closeQuietly(stmt);
            DbUtils.closeQuietly(conn);
        }
        JsonTool.response(resp, jsonRes);
    }

    //登陆
    @Override
    protected void doPut(HttpServletRequest req, HttpServletResponse resp) throws IOException {
        SignInField jsonReq = JsonTool.fetch(req, SignInField.class);
        ResponseField jsonRes;

        if (!jsonReq.Vaild()) {
            jsonRes = new ResponseField("Failed", "Invalid Parameter");
            JsonTool.response(resp, jsonRes);
            return;
        }
        Connection conn = null;
        PreparedStatement stmt = null;
        try {
            DataSource ds = (DataSource) new InitialContext().lookup("java:comp/env/jdbc/postgres");
            assert ds != null;
            conn = ds.getConnection();
            stmt = conn.prepareStatement("SELECT id,salt,salt_pass FROM public.user WHERE nickname=?;");
            stmt.setString(1, jsonReq.Nickname);
            ResultSet res = stmt.executeQuery();
            if (res.next()) {
                String userPass = SHA256.hash256(res.getString("salt") + jsonReq.Password);
                if (userPass.equals(res.getString("salt_pass"))) {
                    req.getSession().setAttribute("nickname", jsonReq.Nickname);
                    req.getSession().setAttribute("uid", res.getLong("id"));
                    jsonRes = new ResponseField("Success", "登陆成功");
                } else {
                    jsonRes = new ResponseField("Failed", "密码错误");
                }
            } else {
                jsonRes = new ResponseField("Failed", "用户名不存在");
            }
            conn.close();
        } catch (SQLException e) {
            switch (e.getSQLState()) {
                default:
                    jsonRes = new ResponseField("Failed", e.getMessage(), String.format("%s", e.getSQLState()));
            }
        } catch (NamingException e) {
            e.printStackTrace();
            jsonRes = new ResponseField("Failed", "服务器资源不足", e.getExplanation());
        } finally {
            DbUtils.closeQuietly(stmt);
            DbUtils.closeQuietly(conn);
        }
        JsonTool.response(resp, jsonRes);
    }

    //退出
    @Override
    protected void doDelete(HttpServletRequest req, HttpServletResponse resp) throws IOException {
        HttpSession session = req.getSession();
        ResponseField jsonRes;
        if (session.getAttribute("uid") == null) {
            jsonRes = new ResponseField("Failed", "User Not Login");
            JsonTool.response(resp, jsonRes);
            return;
        }

        session.invalidate();
        jsonRes = new ResponseField("Success", "User Exit Successful");
        JsonTool.response(resp, jsonRes);
    }

}

class SignInField {
    private static Pattern nickNamePattern = Pattern.compile("^[A-Za-z0-9@_\\-]{5,64}$");
    private static Pattern passwordPattern = Pattern.compile("^[A-Za-z0-9]{64}$");
    String Nickname;
    String Password;

    boolean Vaild() {

        if (Nickname == null || !nickNamePattern.matcher(Nickname).matches()) {
            return false;
        }
        if (Password == null || !passwordPattern.matcher(Password).matches()) {
            return false;
        }
        Nickname = Nickname.toLowerCase();
        Password = Password.toLowerCase();
        return true;
    }
}

